def sum_odd_numbers(numbers):
    sum = 0
    for number in numbers:
        if number % 2 != 0:
            sum += number
    return sum

def factorial(number):
    if number == 0:
        return 1
    else:
        return number * factorial(number - 1)

def find_max_number(numbers):
    max = numbers[0]
    for number in numbers:
        if number > max:
            max = number
    return max

